import './App.css';
import Viva from 'vivagraphjs';
import {useEffect, useRef} from 'react';

var graphGenerator = Viva.Graph.generator();

var graph = graphGenerator.wattsStrogatz(50, 15, 0.05);


function App() {
  const graph_div = useRef(null);

  useEffect( ()=> {
    let viva_layout = Viva.Graph.Layout.forceDirected(graph, {
      springLength : 10,
      springCoeff : 0.0001,
      dragCoeff : 0.1,
      gravity : -1.2,
    });

    let viva_graphics = Viva.Graph.View.svgGraphics();

    let viva_renderer = Viva.Graph.View.renderer(graph, {
      container: graph_div.current,
      graphics: viva_graphics,
      renderLinks: true,
      layout: viva_layout,
      interactive: 'node drag'
    });


    let highlight_neighborhood = (node) => {
      console.log(node);

      viva_graphics.getNodeUI(node.target.node.id).attr("fill", "grey");

      graph.forEachLinkedNode(node.target.node.id, (neighbor)=> {
        viva_graphics.getNodeUI(neighbor.id).attr("fill", "grey");
      });
    };

    let unhighlight_neighborhood = (node) => {
      console.log(node);

      viva_graphics.getNodeUI(node.target.node.id).attr("fill", "black");

      graph.forEachLinkedNode(node.target.node.id, (neighbor)=> {
        viva_graphics.getNodeUI(neighbor.id).attr("fill", "black");
      });
    };


    viva_graphics.node((node) => {
      var ui = Viva.Graph.svg("circle")
        .attr("r", 5)
        //.on("click", (t) => highlight_neighborhood(t))
        .on("mouseover", (t) => highlight_neighborhood(t))
        .on("mouseout", (t) => unhighlight_neighborhood(t));
      return ui;
    });

    viva_graphics.placeNode(function(nodeUI, pos) {
      nodeUI.attr('cx', pos.x).attr('cy', pos.y);
    });

    viva_renderer.run();

    console.log(viva_graphics);



    return () => {
      viva_renderer.dispose();
    };


  }, [])



  return (
    <svg ref={graph_div} style={{width:800, height:600}} >
    </svg>
  );
}

export default App;
