import './App.css';
import Viva from 'vivagraphjs';
import {useEffect, useRef} from 'react';

var graphGenerator = Viva.Graph.generator();

var graph = graphGenerator.wattsStrogatz(100, 15, 0.05);


function App() {
  const graph_div = useRef(null);

  useEffect( ()=> {
    let viva_layout = Viva.Graph.Layout.forceDirected(graph, {
      springLength : 10,
      springCoeff : 0.0001,
      dragCoeff : 0.05,
      gravity : -1.2,
    });

    let viva_graphics = Viva.Graph.View.webglGraphics();

    let viva_renderer = Viva.Graph.View.renderer(graph, {
      container: graph_div.current,
      graphics: viva_graphics,
      renderLinks: true,
      layout: viva_layout,
      interactive: 'node drag'
    });

    viva_renderer.run();


    graph.forEachNode( (node) => {
      viva_graphics.getNodeUI(node.id).size = 20;
      viva_graphics.getNodeUI(node.id).color = 0x00FF11FF;
    });

    var events = Viva.Graph.webglInputEvents(viva_graphics, graph);
    console.log(events);

    events.mouseEnter( (node) => {
      viva_graphics.getNodeUI(node.id).color = 0xFF0011FF;
      //create callback that infects neighbors:
      graph.forEachLinkedNode(node.id, (something) => {
        viva_graphics.getNodeUI(something.id).color = 0xFF0011FF;
      });
    });

    return () => {
      viva_renderer.dispose();
    };


  }, [])



  return (
    <div ref={graph_div} style={{width:800, height:600}} >
    </div>
  );
}

export default App;
