import './App.css';
import Viva from 'vivagraphjs';
import {useRef} from 'react';

var graphGenerator = Viva.Graph.generator();
var graph = graphGenerator.grid(3, 3);


let viva_graphics = Viva.Graph.View.webglGraphics();



function App() {
  let viva_layout = Viva.Graph.Layout.forceDirected(graph, {
    springLength : 15,
    springCoeff : 0.00005,
    dragCoeff : 0.01,
    gravity : -1.5
  });

  const graph_div = useRef(null);
  let viva_renderer = Viva.Graph.View.renderer(graph, {
    container: graph_div.current,
    graphics: viva_graphics,
    renderLinks: true,
    layout: viva_layout,
    interactive: 'node drag'

    });

    viva_renderer.run();
    for (let i=0; i < 30; i++) {
        viva_renderer.zoomOut();
    }


  return (
    <div ref={graph_div} style={{width:400, height:400}} >
    </div>
  );
}

export default App;
